document.querySelectorAll(".nav-item__link").forEach(e=>{e.href===window.location.href&&e.setAttribute("aria-current","page")});document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("openNavBtn"),t=document.getElementById("closeNavBtn"),o=document.getElementById("myNav");e.addEventListener("click",function(){o.style.width="100%"}),t.addEventListener("click",function(){o.style.width="0%"})});document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".nav-outer-container"),t=document.querySelector(".logo"),o=document.querySelector(".logo-text");window.addEventListener("scroll",()=>{window.scrollY>50?(e.classList.add("scrolled"),t.classList.add("scrolled"),o.classList.add("scrolled")):(e.classList.remove("scrolled"),t.classList.remove("scrolled"),o.classList.remove("scrolled"))})});const c=document.querySelectorAll(".nav-item-text");window.addEventListener("scroll",()=>{const e=window.scrollY>50;c.forEach(t=>{e?t.classList.add("scrolled"):t.classList.remove("scrolled")})});const l=document.querySelector("#currentYear"),n=new Date;console.log(n);l.innerText=n.getFullYear();
